class ShotiAPI {
	constructor(apikey) {
		this.apikey = apikey;
	}
	async getShoti() {
		try {
		  return await require('./src/getShoti')(this.apikey);
		} catch (err) {
		  return err
		}
	}
	async addShoti(url) {
		try {
		  return await require('./src/addShoti')(this.apikey, url);
		} catch (err) {
		  return err
		}
	}
	async getTopUsers() {
		try {
		  return await require('./src/getTopUsers')(this.apikey);
		} catch (err) {
		  return err
		}
	}
}

module.exports = ShotiAPI;